customWidget.css:
html, body {
	margin:0;
	padding:0;
	/*width:100%;*/
	/*height:100%;*/
	overflow-x: hidden;
	overflow-y: hidden;
}

customWidget.javascript:
let global_values = [];
let global_id = "";
let defaultFontSize = "50px";
let defaultFontColor = "rgb(0, 0, 0)";
let defaultTextAlign = "left";
let defaultBackgroundColor = "";
let defaultBorderRadius = "";
let defaultButtonMode = "";
let isBold = false;
let defaultVerticalAlign = 'center';
let textMessage = "";
const output = document.querySelector(".output");
const outputContainer = document.querySelector(".container");

document.getElementById("outer").onclick = function () {
  fireEvent("ONCLICK", true);
};

getValue("global_values", (tempVar) => {
  global_values = JSON.parse(tempVar) || [];
  if (global_id != "") getLanguage(global_id);
});

getValue("config_values", (tempVar) => {
  global_id = tempVar.text_id;
  if (global_id != "") getLanguage(global_id);
  
  if ("font_size" in tempVar && typeof tempVar.font_size !== "undefined") {
    defaultFontSize = tempVar.font_size + "px";
  }
  
  if ("font_color" in tempVar && typeof tempVar.font_color !== "undefined") {
    defaultFontColor = tempVar.font_color;
  }
  
  if ("text_align" in tempVar && typeof tempVar.text_align !== "undefined") {
    defaultTextAlign = tempVar.text_align;
  }

  if ("background_color" in tempVar && typeof tempVar.background_color !== "undefined") {
    defaultBackgroundColor = tempVar.background_color;
  }
  
  if ("border_radius" in tempVar && typeof tempVar.border_radius !== "undefined") {
    defaultBorderRadius = tempVar.border_radius + "px";
  }
  
  if ("button_mode" in tempVar && typeof tempVar.button_mode !== "undefined") {
    defaultButtonMode = tempVar.button_mode;
  }
  
  if ("bold" in tempVar && typeof tempVar.bold !== "undefined") {
    isBold = tempVar.bold;
  }
  
  if ("vertical_align" in tempVar && typeof tempVar.vertical_align !== "undefined") {
    defaultVerticalAlign = tempVar.vertical_align;
  }
  
  processInput();
});

function getLanguage(id) {
  const [interfaceData] = global_values.filter((data) => data.id == id);
  if (interfaceData) textMessage = interfaceData.data;
  processInput();
};

function resize_to_fit() {
  let fontSize = window.getComputedStyle(output).fontSize;

  if (output.clientHeight > window.innerHeight) {
    output.style.fontSize = parseFloat(fontSize) - 2 + "px";
    resize_to_fit();
  }
}


function processInput() {
    // console.log(typeof(defaultVerticalAlign), defaultVerticalAlign)
  output.innerHTML = textMessage;
  output.style.fontSize = defaultFontSize; // Default font size
  output.style.color = defaultFontColor;
  output.style.backgroundColor = defaultBackgroundColor;
  output.style.borderRadius = defaultBorderRadius;
  output.style.fontWeight = isBold ? "bold" : "normal";
  outputContainer.style.alignItems = defaultVerticalAlign
  outputContainer.style.justifyContent = defaultTextAlign

  if (defaultButtonMode) {
    output.style.cursor = "pointer";
    console.log("fired");
  } else {
    output.style.cursor = "auto";
  }

  output.style.textAlign = defaultTextAlign;
  resize_to_fit();
}
